(64, 'Ski info-Jahorina', '<p><span><span><strong><span>02.02.2012. god.</span></strong></span></span></p>\r\n<p><span><span><strong><span>Temperatura u 8:00 časova: -13 &deg;C</span></strong></span></span></p>\r\n<p><span><span><strong><span>Brzina vjetra: 6 m/s</span></strong></span></span></p>\r\n<p><span><span><strong><span>Visina snijega: 115 cm</span></strong></span></span></p>\r\n<p><span><span><strong><span>Vrijeme: Sunčano</span></strong></span></span></p>\r\n<p><span><span><strong><span>Vidljivost: Dobra</span></strong></span></span></p>\r\n<ul>\r\n<li><span><span><strong><span>Baby lift:<span>&nbsp;<span>Radi</span></span><span><span>&nbsp;</span><br /></span></span></strong></span></span></li>\r\n</ul>\r\n<ul>\r\n<li><span><span><strong><span>Lift Poljice:&nbsp;<span>Radi</span><span><span>&nbsp;</span><br /></span></span></strong></span></span></li>\r\n<li><span><span><strong><span>Dvosjed Poljice:<span><span>&nbsp;Ne radi</span><br /></span></span></strong></span></span></li>\r\n<li><strong><span><span>&Scaron;estosjed Poljice:&nbsp;<span>R</span></span><span>adi</span><span><br /></span></span></strong></li>\r\n<li><strong><span><span>&Scaron;estosjed Ogorjelica I</span><span>:</span><span><span>R</span></span><span>adi</span><br /></span></strong></li>\r\n<li><strong><span><span>Dvosjed Ogorjelica II:</span><span><span>Radi po potrebi</span></span><span><br /></span></span></strong></li>\r\n<li><strong></strong><strong></strong><strong></strong><strong><span><span>Lift Željo:</span><span>&nbsp;<span>Radi</span></span><span><br /></span></span></strong></li>\r\n<li><strong><span><span>Lift Olimpik:&nbsp;<span>Radi po potrebi</span></span><span><br /></span></span></strong></li>\r\n<li><span><span><strong><span>Dvosjed Skočine:<span>&nbsp;<span>R</span></span><span><span>adi</span><br /></span></span></strong></span></span></li>\r\n<li>Vertikalni transport radi od 9:00 do 16:00 časova</li>\r\n</ul>\r\n<p>&nbsp;</p>', 'Administrator', '2012-02-02 15:52:45', 'sr', '', '', ''),
(66, 'Završen neformalni trilateralni sastanak na Jahorini u Hotelu Termag', '<p><img style="margin: 10px; vertical-align: baseline; float: left;" title="Zavr&scaron;en neformalni trilateralni sastanak na Jahorini" src="http://www.nezavisne.com/slika/slikac.php?slika=20120203125312_126566.jpg" alt="Zavr&scaron;en neformalni trilateralni sastanak na Jahorini" /></p>\r\n<p>&nbsp;</p>\r\n<p>SARAJEVO - Članovi Predsjedni&scaron;tva BiH Željko Kom&scaron;ić, Neboj&scaron;a Radmanović i Bakir Izetbegović, te predsjednici Srbije Boris Tadić i Hrvatske Ivo Josipović okončali su, ne&scaron;to iza 12.00 časova, neformalni trilateralni sastanak na Jahorini.</p>\r\n<p>Zvaničnici su, po okončanju razgovora, krenuli u obilazak Jahorine.</p>\r\n<p>Dio ekipe se do Rajske doline provezao ratracima Olimpijskog centra Jahorina, a drugi automobilima.</p>\r\n<p>Nakon osvježenja u planinskoj kući "Rajska vrata", članovi Predsjedni&scaron;tva BiH i predsjednici Srbije i Hrvatske učestvovaće na konferenciji za novinare koja je zakazana za 14.00 časova u hotelu "Termag".</p>\r\n<p>Ovo je drugi neformalni trilateralni sastanak, a prvi je održan u julu pro&scaron;le godine na Brionima.</p>', 'Administrator', '2012-02-03 13:49:02', 'sr', 'Završen neformalni trilateralni sastanak na Jahorini u Hotelu Termag', 'jahorina, hotel termag, trilateralni sastanak, boris tadic, ivo josipovic', 'Članovi Predsjedni&scaron;tva BiH Željko Kom&scaron;ić, Neboj&scaron;a Radmanović i Bakir Izetbegović, te predsjednici Srbije Boris Tadić i Hrvatske Ivo Josipović okončali su, ne&scaron;to iza 12.00 časova, neformalni trilateralni sastanak na Jahorini.'),
